mapper.sql:
SELECT DISTINCT a.id AS id FROM freshness_batch a LEFT JOIN freshness_batch b ON a.kinds_id = b.kinds_id AND a.group_flag = b.group_flag AND a.create_time <= b.create_time WHERE a.shop_id = #{shopId,jdbcType=INTEGER} AND a.status IN
#{item,jdbcType=INTEGER}
GROUP BY a.kinds_id, a.group_flag, a.create_time HAVING count(b.create_time) <= #{count,jdbcType=INTEGER}

mapper.select:
select

from freshness_batch f
where 1=1

    AND id = #{id,jdbcType=INTEGER}


    AND kinds_id = #{kindsId,jdbcType=INTEGER}


    AND shop_id = #{shopId,jdbcType=INTEGER}


    AND shop_name = #{shopName,jdbcType=VARCHAR}


    AND status = #{status,jdbcType=INTEGER}


    AND expired_time = #{expiredTime,jdbcType=TIMESTAMP}


    AND expired_real_time = #{expiredRealTime,jdbcType=TIMESTAMP}


    AND date(create_time) = date(#{createTime,jdbcType=TIMESTAMP})

SELECT

FROM freshness_batch
WHERE id IN (SELECT DISTINCT batch_id FROM freshness_batch_state a WHERE a.sku_id = #{skuId,jdbcType=INTEGER})
AND shop_id = #{shopId,jdbcType=INTEGER}
AND kinds_id = #{kindId,jdbcType=INTEGER}
order by create_time desc

    limit #{limit,jdbcType=INTEGER}

SELECT min(delay_time)
FROM freshness_batch
WHERE delay_time >= #{now,jdbcType=TIMESTAMP}

AND shop_id = #{shopId,jdbcType=INTEGER}
      AND status IN (1, 5)

SELECT min(expired_time)
FROM freshness_batch
 WHERE expired_time >= #{now,jdbcType=TIMESTAMP}

AND shop_id = #{shopId,jdbcType=INTEGER}
      AND status IN (1, 5)
